import React, { useState, useEffect } from 'react';

const AlertFunction = ({ timer, onComplete, updateTimer, reset }) => {
  const [timeLeft, setTimeLeft] = useState(timer);
  const originalTime = timer; // 元のタイマーの値を保持

  useEffect(() => {
    if (reset) {
      setTimeLeft(originalTime);
      updateTimer(originalTime);
      return;
    }

    if (timeLeft > 0) {
      const timerId = setInterval(() => {
        setTimeLeft(prevTime => {
          const newTime = prevTime - 1;
          updateTimer(newTime);
          return newTime;
        });
      }, 1000);

      return () => clearInterval(timerId);
    } else {
      const audio = new Audio('/notification-sound.mp3');
      audio.play();
      onComplete();
      setTimeLeft(originalTime); // 元のタイマーの値に戻す
      updateTimer(originalTime); // 元のタイマーの値を更新
    }
  }, [timeLeft, onComplete, updateTimer, originalTime, reset]);

  return null;
};

export default AlertFunction;
