import React, { useState } from 'react';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';
import './Calender.css'; // カスタムCSSファイルをインポート

function Calender() {
  const [date, setDate] = useState(new Date());

  const onChange = (newDate) => {
    setDate(newDate);
  };

  const goToToday = () => {
    setDate(new Date());
  };

  return (
    <div>
      <Calendar
        onChange={onChange}
        value={date}
        view="month" // 月ビューのみを表示
        minDetail="month" // 月ビューより詳細なビューを無効化
        navigationLabel={({ date, label, locale, view }) => (
          <button
            type="button"
            onClick={goToToday}
            style={{
              background: 'none',
              border: 'none',
              color: 'inherit',
              font: 'inherit',
              cursor: 'pointer',
              outline: 'inherit',
            }}
          >
            {label}
          </button>
        )}
      />
    </div>
  );
}

export default Calender;
